def listprime(x, y):
	c = [('Primzahlen zwischen ' + str(x) + ' und ' + str(y))]
	for k in range(x, y, 1):
		b = True
		for i in range(2, k, 1):
			if k % i == 0:
				b = False
				break
		if b == True:
			c.append(k)
	return c
print(listprime(124, 190))

a = [1, 5, 2, 5, 2, 1, 623, -213, 2135, 6, -13, 261, -36, 66, -26, 15, 176, -7]
def minimum(a):
	x = int(a[0])
	for element in a:
		if x > int(element):
			x = int(element)
	return x
print(minimum(a))
